import React,{useState,useEffect} from "react";

import getToDos from "../helpers/getToDos";


// react component - todos del usuario 
export default function ToDosUsuario(props){
    let u = props.usuario;
    let misTodos = [];
    const getToDosById = (id) => {
        getToDos()
        .then((data) =>
            {
                for (let index = 0; index < data.todos.length; index++)
                {
                    const element = data.todos[index];

                    if (element.userId === id)
                    {
                        misTodos.push(element)
                    }
                }

            }
        )
        .catch( e => console.log(e))
    }

    const [todos,setTodos] = useState([]);
    useEffect( () => handleTodo(),[])

    const handleTodo = () => {
        //todo generico
        console.log(u.userId);
        const todoInicial = {
            id:99,
            todo:"contenido generico",
            completed:true,
            userId:1
        }
        setTodos([todoInicial]);
        // let v = [];

        // const u = props.usuario;
        // v = getToDosById(v,u.userId);
        // setTodos([v]);
    }
    getToDosById()
    return(
        <div>
            <h1> Mis To Dos ... </h1>
            <ul>
            {todos.map((todo) => (
                <li key={todo.id} >{todo.todo} </li>
            ))}
            </ul>

        </div>
    )
}
