import React,{useEffect,useState} from "react";
import getUserById from '../helpers/getUserById';
export default function Post(props){
  let post = props.post
  let idUser = post.userId;
  console.log(idUser);
  //hooks 
  const [user,setUser] = useState([]);
  useEffect(() => gUser(idUser),[idUser]);
  // logic
  function gUser(idUser){
    getUserById(idUser)
    .then( (data) => { setUser(data.user)} )
    .catch( e => console.log(e))
  }

  return(
    <div>
      <div>
        <li>{post.title}</li>
        <hr></hr>
        <li>{post.body}</li>
      </div>
      <div>

      </div>
    </div>
  );
}
