  let newPost = props.post
  const [user,setUser] = useState({});
  useEffect(()=> userById(newPost.userId),[]);

  const userById = (userId) => { 
    getUserById(userId)
        .then((data) =>
            {
              console.log(data);
              setUser(data);
              console.log('STATE');
              console.log(user);
            }
        )
        .catch( e => console.log('ERROR '+e))
  }

  return(
    <div  className='body'>
      <div className='info-usuario'>
        {/* post.userId para ir y traer la info minima del posteador.
          valido o no userId.
          fetch del object user con id == userId
          render MainUserInfo con el object user como prop        
        */
        }

      </div>
      <ul className='post'>
        <hr></hr>
        <li >{newPost.title} </li> 
        <li> {newPost.body}</li>  
        <li> {newPost.reactions}</li>
        <hr></hr>                      
      </ul>           
    </div>
  );
}
