import React,{useState,useEffect} from "react";
import getUserById from '../helpers/getUserById';

export default function MinimalUserInfo(props){
  let idUser = props.idUser;

  //hooks 
  const [userInfo,setUserInfo] = useState([]);
  useEffect(() => getUserInfo(idUser),[idUser])

  //logic 
  function getUserInfo(idUser){
    getUserById(idUser)
    .then( (data) => setUserInfo(data))
    .catch( e => console.log(e))
  }
  return (
    <div>
      <h2> user id : {props.idUser}</h2>
      <div className="infoUser">
        {
          <ul>
            <li> {userInfo.username} </li>
            <li> {userInfo.firstName} </li>
            <img src={userInfo.image} alt="Profile Img"></img>
          </ul>
        }
      </div>
    </div>
  );
}
