import React,{useState,useEffect} from "react";
import './post.css'
import getComments from "../helpers/getCommentsByPost";

  export default function Post(props){
  let post = props.post;
  //console.log(post);


  function obtenerComentarios(postId){
    getComments(postId)
    .then((data) => setComments(data.comments))
    .catch((e)  => console.log(e))
  }

  const [comments,setComments] = useState([]);

  useEffect(()=> {
    obtenerComentarios(post.id)
  }, [post.id]);
  useEffect( () => { <p> {comments.length} </p>} , [comments])
  return(
    <div className="postText" onClick={() => alert(post.id)}>
      <li className="title">{post.title}</li>
      <li>{post.body}</li>
      <hr></hr>
      <div>
        <img src={'./speech-bubble.png'} alt='comments' className="img"></img>
      </div>

    </div>
  );
}
