import React,{useEffect} from 'react';
import getUserById from '../helpers/getUserById';
import MainUserInfo from '../mainUserInfo/MainUserInfo';

export default function Posts(props){
  let newPost = props.post
  const userById = (userId) => { 
    getUserById(userId)
        .then((data) =>
            {
              return (data);
            }
        )
        .catch( e => console.log(e))
  }
  return(
    <div  className='body'>
      <div className='info-usuario'>
        {/* post.userId para ir y traer la info minima del posteador.
          valido o no userId.
          fetch del object user con id == userId
          render MainUserInfo con el object user como prop        
        */
        }
        <MainUserInfo usuario={userById(newPost.userId)}></MainUserInfo>
      </div>
      <ul className='post'>
        <hr></hr>
        <li >{newPost.title} </li> 
        <li> {newPost.body}</li>  
        <li> {newPost.reactions}</li>
        <hr></hr>                      
      </ul>           
    </div>
  );
}
