  const getCommentsByPost = (x) => {
    getComments(x)
    .then( (data) => { console.log(data)})
    .catch( e => console.log(e))
  }


  return(
    <div className="postText">
      <li className="title">{post.title}</li>
      <li>{post.body}</li>
      <hr></hr>
      { getCommentsByPost(1)}
      <div>
        <img src={'./speech-bubble.png'} alt='comments' className="img"></img>
        <p> reacciones</p>
      </div>
    </div>
  );
}
