import '../toDosUser/toDosUser.css';

import getToDos from "../helpers/getToDos";

import React,{useState,useEffect} from "react";

// react component - todos del usuario 
export default function ToDosUsuario(props){
    // usuario traido desde las props;
    let u = props.usuario;

    const [todos,setTodos] = useState([]);
    useEffect( () => handleTodo(u),[])

    const handleTodo = (u) => {
        let misToDos  = [];
        getToDos()
        .then((data) =>
            {

                for (let index = 0; index < data.todos.length; index++)
                {
                    const element = data.todos[index];

                    if (element.userId === u.id)
                    {
                        misToDos.push(element)
                    }
                }
                setTodos(misToDos);
            }
        )
        .catch( e => console.log(e))


        //todo generico
        // console.log(u.userId);
        // const todoInicial = {
        //     id:99,
        //     todo:"contenido generico",
        //     completed:true,
        //     userId:1
        // }
        // setTodos([todoInicial]);

    }
    return(
        <div className="todo">
            <h1> My to-do's </h1>

            {todos.map((todo) => (
                <ul className='toDosList' key={todo.id}>
                    <hr></hr>
                    <li >{todo.todo} </li>
                    <li>{todo.completed.toString()} </li>
                    <hr></hr>
                </ul> 

            ))}


        </div>
    )
}
