import React, {useState,useEffect} from 'react';
import getPosts from '../helpers/getPosts';

import '../posts/posts.css'
export default function Posts(){

  const [posts,setPosts] = useState([]);
  useEffect( () => handlePosts(),[]);

  function handlePosts(){
    getPosts()

    .then( 
      (data) => {

        setPosts(data.posts)
      }
    )
    .catch( e => console.log(e))
  }
  function goToPost(post){
    //renderizar la page donde solo se muestra la info del post en particular
    console.log(post);
  }
  return(
    <div className='body'>
      <h1> POSTS</h1>
      {posts.map((post) => (
                <ul  className='posts' key={post.id} onClick={()=> goToPost(post)}>
                    <hr></hr>
                    <li >{post.title} </li>
                    <hr></hr>
                    <li> {post.body}</li>  
                    <hr></hr>

                </ul>

            )
          )
      } 
    </div>
  );  
}
