import React,{useState,useEffect} from "react";

import getToDos from "../helpers/getToDos";


// react component - todos del usuario 
export default function ToDosUsuario(props){

    let misToDos = [];
    const getToDosById = (id) => {
        getToDos()
        .then((data) =>
            {
                for (let index = 0; index < data.todos.length; index++)
                {
                    const element = data.todos[index];

                    if (element.userId === id)
                    {
                        misToDos.push(element)
                    }
                }
            }
        )
        .catch( e => console.log(e))
    }

    const [todos,setTodos] = useState([]);
    useEffect( () => {handleTodo()},[])

    const handleTodo = () => {
        // todo generico
        // const todoInicial = {
        //     id:99,
        //     todo:"contenido generico",
        //     completed:true,
        //     userId:1
        // }

        // setTodos([todoInicial]);
        const u = props.usuario;
        getToDosById(u.userId);
        setTodos([misToDos]);
    }

    return(
        <div>
            <h1> Mis To Dos ... </h1>
            <ul>
            {todos.map((todo) => (
                <div key={todo.id}>
                    <li >{todo.todo}</li>
                    <li>{todo.id}</li>

                </div>
            ))}
            </ul>

        </div>
    )
}
