export default function Posts(props){

  console.log(props);
  let newPost = props.post;
  //console.log(newPost);
  const [user,setUser] = useState([]);
  useEffect( () => userById(newPost),[newPost])
  const userById = (id) => { 
    getUserById(id)
        .then((data) =>
            {

              setUser(data);
              //console.log(user);
            }
        )
        .catch( e => console.log('ERROR '+e))
  }

  return(
    <div  className='body'>
      <div className='info-usuario'>
        {/* post.userId para ir y traer la info minima del posteador.
          valido o no userId.
